import React, { useState } from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import { minutesToDuration, secondsToDuration } from "../utils/duration";
import Session from "../pomodoro/Session";
import Timer from "./Timer";

function Pomodoro() {
  // Timer starts out paused
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [focusTime, setFocusTime] = useState(25 * 60);
  const [breakTime, setBreakTime] = useState(5 * 60);
  const [focusRemaining, setFocusRemaining] = useState(0);
  const [breakRemaining, setBreakRemaining] = useState(0);
  const [mode, setMode] = useState("Stop");

  useInterval(
    () => {
      // ToDo: Implement what should happen when the timer is running
      if (mode === "Focus") {
        // play the alarm
        new Audio(`https://bigsoundbank.com/UPLOAD/mp3/1482.mp3`).play();
        const duration = mode === "focus" ? breakTime : focusTime; // select the correct time duration
        setTimeRemaining(duration * 60) // set the time remaining to the new duration
        setMode((prevMode) => (prevMode === "focus" ? "break" : "focus"));
        return;
      }
      setTimeRemaining(timeRemaining - 1)
    },
    isTimerRunning ? 1000 : null
  );

  const stopTimer = () => {
    setSessionActive(false);
    setIsTimerRunning(false);
    setMode("focus");
  }

  function playPause() {
    // if session is false, reset our timeremaining to default
    if (!sessionActive) {
      setSessionActive(true)
      setTimeRemaining(focusTime * 60)
    }
    setIsTimerRunning((prevState) => !prevState);
  }

  const decreaseFocus = () => {
    setFocusTime((lastFocus) => lastFocus - (5 * 60)); 
  };

  const increaseFocus = () => {
    setFocusTime((lastFocus) => lastFocus + (5 * 60)); // take the previous focusDuration value and add 5
  };

  const decreaseBreak = () => {
    setBreakTime((lastBreak) => Math.max(1, lastBreak - 1));
  };

  const increaseBreak = () => {
    setBreakTime((lastBreak) => Math.min(15, lastBreak + 1));
  };

  return (
    <div className="pomodoro">
      <div className="row">
        <div className="col">
          <div className="input-group input-group-lg mb-2">
            <span className="input-group-text" data-testid="duration-focus">
              {/* TODO: Update this text to display the current focus session duration */}
              Focus Duration: {minutesToDuration(focusTime)}
            </span>
            <div className="input-group-append">
              {/* TODO: Implement decreasing focus duration and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="decrease-focus"
                onClick={decreaseFocus}
                disabled={sessionActive || isTimerRunning ? true : false}
              >
                <span className="oi oi-minus" />
              </button>
              {/* TODO: Implement increasing focus duration  and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="increase-focus"
                onClick={increaseFocus}
                disabled={sessionActive || isTimerRunning ? true : false}
              >
                <span className="oi oi-plus" />
              </button>
            </div>
          </div>
        </div>
        <div className="col">
          <div className="float-right">
            <div className="input-group input-group-lg mb-2">
              <span className="input-group-text" data-testid="duration-break">
                {/* TODO: Update this text to display the current break session duration */}
                Break Duration: {minutesToDuration(breakTime)}
              </span>
              <div className="input-group-append">
                {/* TODO: Implement decreasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="decrease-break"
                  onClick={decreaseBreak}
                  disabled={sessionActive || isTimerRunning ? true : false}
                >
                  <span className="oi oi-minus" />
                </button>
                {/* TODO: Implement increasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="increase-break"
                  onClick={increaseBreak}
                  disabled={sessionActive || isTimerRunning ? true : false}
                >
                  <span className="oi oi-plus" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
            >
              <span
                className={classNames({
                  oi: true,
                  "oi-media-play": !isTimerRunning,
                  "oi-media-pause": isTimerRunning,
                })}
              />
            </button>
            {/* TODO: Implement stopping the current focus or break session and disable when there is no active session */}
            <button
              type="button"
              className="btn btn-secondary"
              title="Stop the session"
              onClick={stopTimer}
              disabled={!sessionActive}
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>
      </div>
      <Session mode={mode} isTimerRunning={isTimerRunning} timeRemaining={timeRemaining} sessionActive={sessionActive} focusTime={focusTime} breakTime={breakTime} />
    </div>
  );
}

export default Pomodoro;
